import { useMemo, useState } from "react";
import { COMPANIES } from "../constants";

export default function Hero() {
  const [selectedCompany, setSelectedCompany] = useState("Google");

  const companyOptions = useMemo(() => {
    const fixedCompanies = ["Google", "Infosys", "Microsoft", "TCS", "Accenture"];
    const otherCompanies = COMPANIES.map((company) => company.name).filter(
      (name) => !fixedCompanies.includes(name)
    );

    return [...fixedCompanies, ...otherCompanies, "Other Company"];
  }, []);

  const marqueeItems = [...companyOptions, ...companyOptions];

  return (
    <section className="relative flex min-h-screen flex-col items-center justify-center overflow-hidden px-5 pb-[90px] pt-[130px] text-center md:px-10">
      <div className="pointer-events-none absolute inset-0 bg-gradient-to-b from-[#090909] to-[#0f0f0f]" />
      <div className="pointer-events-none absolute -top-28 left-1/2 h-[520px] w-[900px] -translate-x-1/2 rounded-full bg-[#c8ff00]/10 blur-[120px]" />
      <div className="pointer-events-none absolute -bottom-24 right-0 h-[360px] w-[560px] rounded-full bg-[#00ffcc]/10 blur-[130px]" />
      <div className="pointer-events-none absolute inset-0 [background-image:linear-gradient(rgba(255,255,255,0.03)_1px,transparent_1px),linear-gradient(90deg,rgba(255,255,255,0.03)_1px,transparent_1px)] [background-size:60px_60px]" />
      <div className="relative mb-8 inline-flex items-center rounded-full border border-[#c8ff00]/30 bg-[#c8ff00]/10 px-[18px] py-[9px] text-[11px] font-semibold uppercase tracking-[1.5px] text-[#c8ff00]">âœ¦ HumanResourcesOnboarding&Policy ExplainerBot</div>
      <h1 className="relative mb-4 max-w-[680px] font-['Syne'] text-[28px] font-semibold leading-[1.25] tracking-normal text-white">
        Simplify <em className="not-italic text-[#c8ff00]">Employee Onboarding</em> and HR Policy Support
      </h1>
      <p className="relative mb-10 max-w-[640px] text-[17px] leading-[1.7] text-white/60">Your AI assistant answers policy questions instantly and guides employees through onboarding with consistent, approved information.</p>
      <div className="relative mb-6 w-full overflow-hidden border-y border-white/10 bg-gradient-to-b from-white/[0.01] to-transparent py-6">
        <p className="mb-4 text-center text-[11px] uppercase tracking-[2px] text-white/60">Companies</p>
        <div className="overflow-hidden">
          <div className="flex w-max animate-marquee">
            {marqueeItems.map((companyName, index) => (
              <div className="flex whitespace-nowrap px-9 font-['Syne'] text-[14px] font-bold text-white/70" key={`${companyName}-${index}`}>
                <span className="mr-2.5 mt-[8px] h-1.5 w-1.5 shrink-0 rounded-full bg-[#c8ff00]" />
                {companyName}
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="relative mb-4 w-full max-w-[620px]">
        <select
          value={selectedCompany}
          onChange={(event) => setSelectedCompany(event.target.value)}
          className="h-12 w-full rounded-xl border border-white/10 bg-[#121212] px-4 text-[14px] text-white outline-none"
        >
          {companyOptions.map((companyName) => (
            <option key={companyName} value={companyName}>
              {companyName}
            </option>
          ))}
        </select>
      </div>

      {selectedCompany === "Other Company" && (
        <div className="relative flex w-full max-w-[620px] flex-col gap-3 rounded-2xl border border-white/10 bg-white/[0.03] p-3 md:flex-row md:items-center">
          <input
            type="text"
            placeholder="Search for chat bot"
            className="h-12 flex-1 rounded-xl border border-white/10 bg-[#121212] px-4 text-[14px] text-white outline-none placeholder:text-white/50"
          />
          <button
            type="button"
            className="h-12 rounded-xl bg-[#c8ff00] px-6 font-['Syne'] text-[14px] font-bold text-black shadow-[0_12px_26px_rgba(200,255,0,0.25)] transition hover:-translate-y-0.5"
          >
            Search
          </button>
        </div>
      )}
    </section>
  );
}
