import { useMemo, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

export default function ChatPage() {
  const navigate = useNavigate();
  const { companyName } = useParams();
  const selectedCompany = useMemo(() => decodeURIComponent(companyName || "Company"), [companyName]);

  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([
    {
      role: "bot",
      text: `Hi! I'm your HR Policy ExplainerBot for ${selectedCompany}. Ask me anything about onboarding, leaves, policies, and compliance.`,
    },
  ]);

  const handleSend = () => {
    const trimmedInput = input.trim();
    if (!trimmedInput) return;

    setMessages((previousMessages) => [
      ...previousMessages,
      { role: "user", text: trimmedInput },
      {
        role: "bot",
        text: `Thanks! This is a frontend demo for ${selectedCompany}. Connect your backend to return policy-aware responses here.`,
      },
    ]);
    setInput("");
  };

  return (
    <div className="min-h-screen bg-[#090909] px-5 py-8 text-white md:px-10">
      <div className="mx-auto max-w-5xl">
        <div className="mb-6 flex items-center justify-between rounded-2xl border border-white/10 bg-white/[0.03] p-4 md:p-5">
          <div>
            <p className="text-[11px] uppercase tracking-[2px] text-[#c8ff00]">Selected Company</p>
            <h1 className="font-['Syne'] text-2xl font-bold md:text-3xl">{selectedCompany} HR Chatbot</h1>
          </div>
          <button
            type="button"
            onClick={() => navigate("/")}
            className="rounded-lg border border-white/15 px-4 py-2 text-sm font-semibold text-white transition hover:border-white/30 hover:bg-white/5"
          >
            Back
          </button>
        </div>

        <div className="overflow-hidden rounded-2xl border border-white/10 bg-[#111111]">
          <div className="h-[62vh] space-y-3 overflow-y-auto p-4 md:p-6">
            {messages.map((message, index) => (
              <div
                key={`${message.role}-${index}`}
                className={`max-w-[85%] rounded-xl px-4 py-3 text-[14px] leading-relaxed ${
                  message.role === "user"
                    ? "ml-auto border border-white/10 bg-white/[0.07] text-white/90"
                    : "border border-[#c8ff00]/20 bg-[#c8ff00]/10 text-white"
                }`}
              >
                {message.role === "bot" && (
                  <p className="mb-1 text-[10px] font-bold uppercase tracking-[1px] text-[#c8ff00]">HR Bot</p>
                )}
                {message.text}
              </div>
            ))}
          </div>

          <div className="border-t border-white/10 bg-[#0f0f0f] p-4 md:p-5">
            <div className="flex flex-col gap-3 md:flex-row md:items-center">
              <input
                type="text"
                value={input}
                onChange={(event) => setInput(event.target.value)}
                onKeyDown={(event) => {
                  if (event.key === "Enter") {
                    handleSend();
                  }
                }}
                placeholder="Type your onboarding or policy question..."
                className="h-12 flex-1 rounded-xl border border-white/10 bg-[#121212] px-4 text-[14px] text-white outline-none placeholder:text-white/50"
              />
              <button
                type="button"
                onClick={handleSend}
                className="h-12 rounded-xl bg-[#c8ff00] px-6 font-['Syne'] text-[14px] font-bold text-black shadow-[0_12px_26px_rgba(200,255,0,0.25)] transition hover:-translate-y-0.5"
              >
                Send
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
