import { HOW_STEPS } from "../constants";

export default function HowSection() {
  return (
    <section className="ll-section ll-how-section">
      <div className="ll-container">
        <p className="ll-section-label">Implementation</p>
        <h2 className="ll-section-title">How We Set Up<br />Your HR Assistant</h2>
        <div className="ll-how-steps">
          {HOW_STEPS.map((step) => (
            <div className="ll-how-step" key={step.num}>
              <div className="ll-step-number">{step.num}</div>
              <div className="ll-step-icon">{step.icon}</div>
              <h3>{step.title}</h3>
              <p>{step.desc}</p>
              <br />
              <a href="#" className="ll-explore-link">Learn More â†’</a>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
