import { SERVE_CARDS } from "../constants";

export default function ServeSection() {
  return (
    <section className="ll-section ll-serve-section">
      <div className="ll-container">
        <p className="ll-section-label">Who It's For</p>
        <h2 className="ll-section-title">Built for teams that need<br />clear HR answers fast</h2>
        <div className="ll-serve-grid">
          {SERVE_CARDS.map((card) => (
            <div className="ll-serve-card" key={card.title}>
              <div className="ll-serve-emoji">{card.emoji}</div>
              <div className="ll-serve-body">
                <h3>{card.title}</h3>
                <p>{card.desc}</p>
                <a href="#" className="ll-explore-link">Explore â†’</a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
