import { useMemo, useState } from "react";
import { COMPANIES } from "../constants";

export default function ServicesSection() {
  const [query, setQuery] = useState("");

  const filteredCompanies = useMemo(() => {
    const search = query.trim().toLowerCase();
    if (!search) return COMPANIES;

    return COMPANIES.filter((company) => {
      const text = `${company.name} ${company.industry} ${company.location}`.toLowerCase();
      return text.includes(search);
    });
  }, [query]);

  return (
    <section className="ll-section ll-services-section">
      <div className="ll-container">
        <p className="ll-section-label">Company Directory</p>
        <h2 className="ll-section-title">Find a Company and Ask the Bot</h2>
        <p className="ll-section-sub">Frontend demo only. You can connect backend and chatbot logic later.</p>
        <div className="ll-email-form" style={{ maxWidth: 560, marginTop: 22 }}>
          <input
            type="text"
            placeholder="Search company by name, industry, or location"
            value={query}
            onChange={(event) => setQuery(event.target.value)}
          />
          <button type="button">Search</button>
        </div>
        <div className="ll-services-grid">
          {filteredCompanies.length > 0 ? (
            filteredCompanies.map((company) => (
              <div className="ll-service-card" key={company.name}>
                <div className="ll-service-icon">{company.icon}</div>
                <h3>{company.name}</h3>
                <p>{company.industry} â€¢ {company.location}</p>
                <p>{company.desc}</p>
                <a href="#" className="ll-explore-link">Ask Chatbot â†’</a>
              </div>
            ))
          ) : (
            <div className="ll-service-card">
              <div className="ll-service-icon">ðŸ”Ž</div>
              <h3>No companies found</h3>
              <p>Try a different keyword to find the company and continue with chatbot support.</p>
            </div>
          )}
        </div>
      </div>
    </section>
  );
}
