import { TECHS } from "../constants";

export default function Marquee() {
  const items = [...TECHS, ...TECHS];

  return (
    <div className="ll-marquee-section">
      <p className="ll-marquee-label">Technologies Behind HR Onboarding & Policy Assistance</p>
      <div className="ll-marquee-wrap">
        <div className="ll-marquee-track">
          {items.map((tech, index) => (
            <div className="ll-marquee-item" key={`${tech}-${index}`}>
              <span className="ll-marquee-dot" />
              {tech}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
